import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';
import { Calendar, ChevronLeft, ChevronRight, Dumbbell, Utensils, MapPin, Clock, Plus, X, Users } from 'lucide-react';

export default function Schedule({ userId, userRole }) {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userTeamId, setUserTeamId] = useState(null);
  const [view, setView] = useState('month');
  const [currentDate, setCurrentDate] = useState(new Date());

  // Quick add panel state
  const [showQuickAdd, setShowQuickAdd] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);
  const [eventCategory, setEventCategory] = useState('team'); // 'team', 'workout', 'meal'
  const [quickAddMode, setQuickAddMode] = useState('select'); // 'select' or 'create'

  // Data for dropdowns
  const [teams, setTeams] = useState([]);
  const [trainingPrograms, setTrainingPrograms] = useState([]);
  const [mealPlans, setMealPlans] = useState([]);

  useEffect(() => {
    fetchUserTeam();
    if (userRole === 'admin' || userRole === 'coach') {
      fetchTeams();
      fetchTrainingPrograms();
      fetchMealPlans();
    }
  }, [userId, userRole]);

  useEffect(() => {
    if (userTeamId || userRole === 'admin' || userRole === 'coach') {
      fetchEvents();
    }
  }, [userTeamId, userRole]);

  const fetchUserTeam = async () => {
    const { data, error } = await supabase
      .from('team_members')
      .select('team_id')
      .eq('user_id', userId)
      .limit(1)
      .single();

    if (!error && data) {
      setUserTeamId(data.team_id);
    }
    setLoading(false);
  };

  const fetchEvents = async () => {
    let query = supabase
      .from('schedule_events')
      .select(`
        *,
        teams(name),
        training_programs(id, name),
        meal_plans(id, name)
      `);

    if (userRole === 'player' && userTeamId) {
      query = query.eq('team_id', userTeamId);
    }

    const { data, error } = await query;
    if (!error && data) {
      setEvents(data);
    }
    setLoading(false);
  };

  const fetchTeams = async () => {
    const { data } = await supabase.from('teams').select('id, name').order('name');
    if (data) setTeams(data);
  };

  const fetchTrainingPrograms = async () => {
    const { data } = await supabase.from('training_programs').select('id, name').order('name');
    if (data) setTrainingPrograms(data);
  };

  const fetchMealPlans = async () => {
    const { data } = await supabase.from('meal_plans').select('id, name').order('name');
    if (data) setMealPlans(data);
  };

  const handleDayClick = (date) => {
    if (userRole === 'admin' || userRole === 'coach') {
      setSelectedDate(date);
      setShowQuickAdd(true);
      setEventCategory('team');
      setQuickAddMode('select');
    }
  };

  const closeQuickAdd = () => {
    setShowQuickAdd(false);
    setSelectedDate(null);
    setEventCategory('team');
    setQuickAddMode('select');
  };

  const goToPrevious = () => {
    const newDate = new Date(currentDate);
    if (view === 'month') {
      newDate.setMonth(newDate.getMonth() - 1);
    } else {
      newDate.setDate(newDate.getDate() - 7);
    }
    setCurrentDate(newDate);
  };

  const goToNext = () => {
    const newDate = new Date(currentDate);
    if (view === 'month') {
      newDate.setMonth(newDate.getMonth() + 1);
    } else {
      newDate.setDate(newDate.getDate() + 7);
    }
    setCurrentDate(newDate);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <p className="text-gray-600">Loading schedule...</p>
      </div>
    );
  }

  if (userRole === 'player' && !userTeamId) {
    return (
      <div className="bg-white rounded-lg shadow p-12 text-center">
        <Calendar className="mx-auto text-gray-300 mb-4" size={48} />
        <h2 className="text-2xl font-bold text-gray-900 mb-4">No Team Assigned</h2>
        <p className="text-gray-600">You need to be assigned to a team to see the schedule.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900">Schedule</h2>
          <p className="text-gray-600 mt-1">
            {userRole === 'player' ? 'Your team schedule' : 'All team schedules'}
          </p>
        </div>
      </div>

      {/* Quick Add Panel */}
      {showQuickAdd && selectedDate && (
        <QuickAddPanel
          selectedDate={selectedDate}
          eventCategory={eventCategory}
          setEventCategory={setEventCategory}
          mode={quickAddMode}
          setMode={setQuickAddMode}
          teams={teams}
          trainingPrograms={trainingPrograms}
          mealPlans={mealPlans}
          onClose={closeQuickAdd}
          onSuccess={() => {
            closeQuickAdd();
            fetchEvents();
          }}
        />
      )}

      {/* Calendar */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-4 border-b border-gray-200 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button onClick={goToPrevious} className="p-2 hover:bg-gray-100 rounded-lg transition">
              <ChevronLeft size={20} />
            </button>
            <button onClick={goToNext} className="p-2 hover:bg-gray-100 rounded-lg transition">
              <ChevronRight size={20} />
            </button>
            <button onClick={goToToday} className="px-3 py-1 text-sm font-medium text-gray-700 hover:bg-gray-100 rounded-lg transition">
              Today
            </button>
            <h3 className="text-xl font-bold text-gray-900">
              {currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
            </h3>
          </div>

          <div className="flex items-center space-x-2">
            <button
              onClick={() => setView('week')}
              className={`px-4 py-2 text-sm font-medium rounded-lg transition ${
                view === 'week' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              Week
            </button>
            <button
              onClick={() => setView('month')}
              className={`px-4 py-2 text-sm font-medium rounded-lg transition ${
                view === 'month' ? 'bg-blue-600 text-white' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              Month
            </button>
          </div>
        </div>

        <div className="p-6">
          {view === 'month' ? (
            <MonthView 
              currentDate={currentDate} 
              events={events} 
              userRole={userRole}
              onDayClick={handleDayClick}
            />
          ) : (
            <WeekView 
              currentDate={currentDate} 
              events={events} 
              userRole={userRole}
              onDayClick={handleDayClick}
            />
          )}
        </div>
      </div>
    </div>
  );
}

function QuickAddPanel({ selectedDate, eventCategory, setEventCategory, mode, setMode, teams, trainingPrograms, mealPlans, onClose, onSuccess }) {
  const [formData, setFormData] = useState({
    team_id: '',
    event_type: 'practice',
    opponent: '',
    event_time: '16:00',
    location: '',
    address: '',
    home_away: null,
    is_optional: false,
    notes: '',
    training_program_id: '',
    meal_plan_id: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const teamTemplates = {
    practice: { name: 'Practice', data: { event_type: 'practice', opponent: 'Team Practice', event_time: '16:00', location: 'Home Field' }},
    game: { name: 'Game', data: { event_type: 'game', opponent: '', event_time: '18:00', location: '', home_away: 'home' }},
    tournament: { name: 'Tournament', data: { event_type: 'game', opponent: 'Tournament', event_time: '09:00', location: '' }},
    scrimmage: { name: 'Scrimmage', data: { event_type: 'practice', opponent: 'Scrimmage', event_time: '16:00', location: 'Home Field' }}
  };

  const applyTemplate = (templateKey) => {
    setFormData({ ...formData, ...teamTemplates[templateKey].data });
  };

  const handleAssignExisting = async (selectedId, type) => {
    setLoading(true);
    setError('');

    const eventData = {
      event_date: selectedDate,
      event_type: type, // 'workout' or 'meal'
      opponent: type === 'workout' ? 'Training Session' : 'Nutrition Plan',
      event_time: '00:00',
      location: 'N/A',
      team_id: teams[0]?.id || null,
      training_program_id: type === 'workout' ? selectedId : null,
      meal_plan_id: type === 'meal' ? selectedId : null
    };

    const { error: insertError } = await supabase.from('schedule_events').insert(eventData);

    if (insertError) {
      setError(insertError.message);
      setLoading(false);
