// MonthView, WeekView, CalendarDay, EventBadge components remain the same as before
function MonthView({ currentDate, events, userRole, onDayClick }) {
  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startDate = new Date(firstDay);
  startDate.setDate(startDate.getDate() - firstDay.getDay());

  const weeks = [];
  let currentWeek = [];
  const currentDateIter = new Date(startDate);

  while (currentDateIter <= lastDay || currentWeek.length > 0) {
    if (currentWeek.length === 7) {
      weeks.push(currentWeek);
      currentWeek = [];
    }
    currentWeek.push(new Date(currentDateIter));
    currentDateIter.setDate(currentDateIter.getDate() + 1);

    if (weeks.length === 6 && currentWeek.length === 7) break;
  }
  if (currentWeek.length > 0) weeks.push(currentWeek);

  return (
    <div>
      <div className="grid grid-cols-7 gap-px mb-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className="text-center text-sm font-semibold text-gray-600 py-2">
            {day}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-px bg-gray-200 border border-gray-200">
        {weeks.map((week, weekIdx) => (
          <React.Fragment key={weekIdx}>
            {week.map((date, dayIdx) => {
              const dateStr = date.toISOString().split('T')[0];
              const dayEvents = events.filter(e => e.event_date === dateStr);
              const isCurrentMonth = date.getMonth() === month;
              const isToday = dateStr === new Date().toISOString().split('T')[0];

              return (
                <CalendarDay
                  key={dayIdx}
                  date={date}
                  events={dayEvents}
                  isCurrentMonth={isCurrentMonth}
                  isToday={isToday}
                  userRole={userRole}
                  onDayClick={onDayClick}
                />
              );
            })}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}

function WeekView({ currentDate, events, userRole, onDayClick }) {
  const startOfWeek = new Date(currentDate);
  startOfWeek.setDate(currentDate.getDate() - currentDate.getDay());

  const week = [];
  for (let i = 0; i < 7; i++) {
    const date = new Date(startOfWeek);
    date.setDate(startOfWeek.getDate() + i);
    week.push(date);
  }

  return (
    <div>
      <div className="grid grid-cols-7 gap-px mb-2">
        {week.map(date => (
          <div key={date} className="text-center py-2">
            <div className="text-sm font-semibold text-gray-600">
              {date.toLocaleDateString('en-US', { weekday: 'short' })}
            </div>
            <div className={`text-lg font-bold ${
              date.toISOString().split('T')[0] === new Date().toISOString().split('T')[0]
                ? 'text-blue-600'
                : 'text-gray-900'
            }`}>
              {date.getDate()}
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-px bg-gray-200 border border-gray-200">
        {week.map((date, idx) => {
          const dateStr = date.toISOString().split('T')[0];
          const dayEvents = events.filter(e => e.event_date === dateStr);
          const isToday = dateStr === new Date().toISOString().split('T')[0];

          return (
            <CalendarDay
              key={idx}
              date={date}
              events={dayEvents}
              isCurrentMonth={true}
              isToday={isToday}
              userRole={userRole}
              isWeekView={true}
              onDayClick={onDayClick}
            />
          );
        })}
      </div>
    </div>
  );
}

function CalendarDay({ date, events, isCurrentMonth, isToday, userRole, isWeekView = false, onDayClick }) {
  const [isHovered, setIsHovered] = useState(false);
  const dateStr = date.toISOString().split('T')[0];
  const canAddEvents = userRole === 'admin' || userRole === 'coach';

  // Separate events by type
  const teamEvents = events.filter(e => e.event_type === 'game' || e.event_type === 'practice');
  const workoutEvents = events.filter(e => e.event_type === 'workout');
  const mealEvents = events.filter(e => e.event_type === 'meal');

  return (
    <div
      className={`bg-white p-2 min-h-[100px] ${isWeekView ? 'min-h-[150px]' : ''} ${
        !isCurrentMonth ? 'opacity-50' : ''
      } ${isToday ? 'ring-2 ring-blue-600 ring-inset' : ''} relative group`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="flex items-center justify-between mb-1">
        <span className={`text-sm font-semibold ${
          isToday ? 'bg-blue-600 text-white w-6 h-6 rounded-full flex items-center justify-center text-xs' : 'text-gray-900'
        }`}>
          {date.getDate()}
        </span>

        {canAddEvents && isHovered && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              onDayClick(dateStr);
            }}
            className="bg-blue-600 text-white p-1 rounded hover:bg-blue-700 transition text-xs opacity-0 group-hover:opacity-100"
          >
            <Plus size={14} />
          </button>
        )}
      </div>

      <div className="space-y-1">
        {teamEvents.map(event => (
          <EventBadge key={event.id} event={event} userRole={userRole} />
        ))}
        {workoutEvents.map(event => (
          <WorkoutBadge key={event.id} event={event} userRole={userRole} />
        ))}
        {mealEvents.map(event => (
          <MealBadge key={event.id} event={event} userRole={userRole} />
        ))}
      </div>
    </div>
  );
}

