function ScheduleTab({ teams }) {
  const [showForm, setShowForm] = useState(false);
  const [events, setEvents] = useState([]);
  const [trainingPrograms, setTrainingPrograms] = useState([]);
  const [mealPlans, setMealPlans] = useState([]);

  useEffect(() => { 
    fetchEvents(); 
    fetchTrainingPrograms();
    fetchMealPlans();
  }, []);

  const fetchEvents = async () => {
    const { data, error } = await supabase
      .from('schedule_events')
      .select('*, teams(name), training_programs(name), meal_plans(name)')
      .order('event_date', { ascending: true });
    if (!error) setEvents(data);
  };

  const fetchTrainingPrograms = async () => {
    const { data, error } = await supabase
      .from('training_programs')
      .select('id, name')
      .order('name');
    if (!error) setTrainingPrograms(data || []);
  };

  const fetchMealPlans = async () => {
    const { data, error } = await supabase
      .from('meal_plans')
      .select('id, name')
      .order('name');
    if (!error) setMealPlans(data || []);
  };

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold text-gray-900">Schedule Events</h3>
        <button onClick={() => setShowForm(true)} className="bg-blue-600 text-white px-4 py-2 rounded-lg font-medium hover:bg-blue-700 transition flex items-center space-x-2">
          <Plus size={18} /><span>Add Event</span>
        </button>
      </div>
      <div className="space-y-2">
        {events.map(event => (
          <div key={event.id} className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center space-x-3">
              <span className="font-semibold text-gray-900">{event.opponent}</span>
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${event.event_type === 'game' ? 'bg-blue-100 text-blue-700' : 'bg-green-100 text-green-700'}`}>{event.event_type}</span>
              {event.is_optional && <span className="px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-700">Optional - RSVP</span>}
              {event.training_programs && (
                <span className="px-2 py-1 rounded-full text-xs font-medium bg-purple-100 text-purple-700 flex items-center space-x-1">
                  <span>ðŸ’ª</span>
                  <span>{event.training_programs.name}</span>
                </span>
              )}
              {event.meal_plans && (
                <span className="px-2 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-700 flex items-center space-x-1">
                  <span>ðŸ½ï¸</span>
                  <span>{event.meal_plans.name}</span>
                </span>
              )}
            </div>
            <div className="text-sm text-gray-600 mt-1">{new Date(event.event_date).toLocaleDateString()} &bull; {event.event_time} &bull; {event.location}</div>
            <div className="text-xs text-gray-500 mt-1">Team: {event.teams.name}</div>
          </div>
        ))}
      </div>
      {showForm && <CreateScheduleModal teams={teams} trainingPrograms={trainingPrograms} mealPlans={mealPlans} onClose={() => setShowForm(false)} onSuccess={() => { setShowForm(false); fetchEvents(); }} />}
    </div>
  );
}

