/* ---- CREATE TRAINING PROGRAM MODAL - WITH SINGLE WORKOUT OPTION ---- */

function CreateTrainingProgramModal({ onClose, onSuccess }) {
  const [programType, setProgramType] = useState('multi-day');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [durationWeeks, setDurationWeeks] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault(); 
    setLoading(true); 
    setError('');

    const { data: { user } } = await supabase.auth.getUser();

    // Create the program
    const { data: programData, error: insertError } = await supabase
      .from('training_programs')
      .insert({
        name, 
        description: description || null,
        duration_weeks: programType === 'multi-day' && durationWeeks ? parseInt(durationWeeks) : null,
        created_by: user?.id
      })
      .select()
      .single();

    if (insertError) { 
      setError(insertError.message); 
      setLoading(false); 
      return;
    }

    // If single workout, auto-create Day 1
    if (programType === 'single' && programData) {
      const { error: dayError } = await supabase
        .from('training_days')
        .insert({
          program_id: programData.id,
          day_number: 1,
          title: name,
          notes: 'Single workout session'
        });

      if (dayError) {
        console.error('Error creating day:', dayError);
        setError('Program created but failed to create workout day');
        setLoading(false);
        return;
      }
    }

    onSuccess();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-lg w-full">
        <div className="border-b border-gray-200 p-6 flex items-center justify-between">
          <h3 className="text-xl font-bold text-gray-900">Create Training Program</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600"><X size={24} /></button>
        </div>
        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          {error && <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded text-sm">{error}</div>}

          {/* Program Type Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Program Type *
            </label>
            <div className="grid grid-cols-2 gap-3">
              <label className={`flex items-start space-x-3 p-4 border-2 rounded-lg cursor-pointer transition ${programType === 'single' ? 'border-blue-600 bg-blue-50' : 'border-gray-300 hover:border-gray-400'}`}>
                <input
                  type="radio"
                  value="single"
                  checked={programType === 'single'}
                  onChange={(e) => setProgramType(e.target.value)}
                  className="w-4 h-4 mt-0.5 text-blue-600 border-gray-300 focus:ring-blue-500"
                />
                <div>
                  <span className="text-sm font-semibold text-gray-900">Single Workout</span>
                  <p className="text-xs text-gray-600 mt-1">One training session</p>
                </div>
              </label>
              <label className={`flex items-start space-x-3 p-4 border-2 rounded-lg cursor-pointer transition ${programType === 'multi-day' ? 'border-blue-600 bg-blue-50' : 'border-gray-300 hover:border-gray-400'}`}>
                <input
                  type="radio"
                  value="multi-day"
                  checked={programType === 'multi-day'}
                  onChange={(e) => setProgramType(e.target.value)}
                  className="w-4 h-4 mt-0.5 text-blue-600 border-gray-300 focus:ring-blue-500"
                />
                <div>
                  <span className="text-sm font-semibold text-gray-900">Multi-Day Program</span>
                  <p className="text-xs text-gray-600 mt-1">Multiple days/weeks</p>
                </div>
              </label>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {programType === 'single' ? 'Workout Name' : 'Program Name'} *
            </label>
            <input 
              type="text" 
              required 
              placeholder={programType === 'single' ? 'e.g., Power Hitting Session' : 'e.g., Off-Season Hitting Program'} 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" 
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Description</label>
            <textarea 
              placeholder={programType === 'single' ? 'What is this workout for?' : 'What is this program for?'} 
              value={description} 
              onChange={(e) => setDescription(e.target.value)} 
              rows="2" 
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" 
            />
          </div>

          {programType === 'multi-day' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Duration (weeks)</label>
              <input 
                type="number" 
                min="1" 
                placeholder="e.g., 8" 
                value={durationWeeks} 
                onChange={(e) => setDurationWeeks(e.target.value)} 
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500" 
              />
            </div>
          )}

          <div className="bg-blue-50 border border-blue-200 text-blue-800 px-4 py-3 rounded text-sm">
            {programType === 'single' 
              ? 'ðŸ’ª After creating, you\'ll add exercises to this workout session.'
              : 'ðŸ“… After creating the program, expand it to add multiple days and exercises.'
            }
          </div>

          <div className="flex space-x-3 pt-4">
            <button 
              type="button" 
              onClick={onClose} 
              className="flex-1 border border-gray-300 text-gray-700 py-3 rounded-lg font-medium hover:bg-gray-50 transition"
            >
              Cancel
            </button>
            <button 
              type="submit" 
              disabled={loading} 
              className="flex-1 bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition disabled:opacity-50"
            >
              {loading ? 'Creating...' : (programType === 'single' ? 'Create Workout' : 'Create Program')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

