function TeamCard({ team, refreshTeams }) {
  const [showEditTeam, setShowEditTeam] = useState(false);

  const handleDeleteTeam = async () => {
    if (!window.confirm(`Delete team "${team.name}"? This will remove all team assignments and team-related data.`)) return;

    const { error } = await supabase
      .from('teams')
      .delete()
      .eq('id', team.id);

    if (error) {
      alert('Error deleting team: ' + error.message);
    } else {
      alert('Team deleted successfully');
      refreshTeams();
    }
  };

  return (
    <>
      <div className="bg-gray-50 rounded-lg p-6 hover:bg-gray-100 transition">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-4 flex-1">
            {team.photo_url ? (
              <img
                src={team.photo_url}
                alt={team.name}
                className="w-16 h-16 rounded-lg object-cover"
              />
            ) : (
              <div className="w-16 h-16 bg-blue-600 rounded-lg flex items-center justify-center">
                <Users className="text-white" size={32} />
              </div>
            )}
            <div className="flex-1">
              <h4 className="text-xl font-semibold text-gray-900">{team.name}</h4>
              {team.description && (
                <p className="text-sm text-gray-600 mt-1">{team.description}</p>
              )}
              <p className="text-xs text-gray-500 mt-2">
                Created {new Date(team.created_at).toLocaleDateString()}
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-2 ml-4">
            <button
              onClick={() => setShowEditTeam(true)}
              className="text-blue-600 hover:text-blue-800 p-2 rounded-lg hover:bg-blue-50 transition"
              title="Edit Team"
            >
              <Edit size={18} />
            </button>
            <button
              onClick={handleDeleteTeam}
              className="text-red-600 hover:text-red-800 p-2 rounded-lg hover:bg-red-50 transition"
              title="Delete Team"
            >
              <Trash2 size={18} />
            </button>
          </div>
        </div>
      </div>

      {showEditTeam && (
        <EditTeamModal
          team={team}
          onClose={() => setShowEditTeam(false)}
          onSuccess={() => {
            setShowEditTeam(false);
            refreshTeams();
          }}
        />
      )}
    </>
  );
}

// ============================================
// EDIT TEAM MODAL
// ============================================

function EditTeamModal({ team, onClose, onSuccess }) {
  const [name, setName] = useState(team.name);
  const [description, setDescription] = useState(team.description || '');
  const [photoUrl, setPhotoUrl] = useState(team.photo_url || '');
  const [uploading, setUploading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handlePhotoUpload = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      alert('Please select an image file');
      return;
    }

    setUploading(true);

    try {
      const fileExt = file.name.split('.').pop();
      const fileName = `${team.id}-${Date.now()}.${fileExt}`;
      const filePath = `team-photos/${fileName}`;

      // Upload to Supabase Storage
      const { error: uploadError } = await supabase.storage
        .from('avatars')
        .upload(filePath, file, { upsert: true });

      if (uploadError) throw uploadError;

      // Get public URL
      const { data: { publicUrl } } = supabase.storage
        .from('avatars')
        .getPublicUrl(filePath);

      setPhotoUrl(publicUrl);
    } catch (err) {
      alert('Error uploading photo: ' + err.message);
    } finally {
      setUploading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const { error: updateError } = await supabase
      .from('teams')
      .update({
        name,
        description: description || null,
        photo_url: photoUrl || null
      })
      .eq('id', team.id);

    if (updateError) {
      setError(updateError.message);
      setLoading(false);
    } else {
      alert('Team updated successfully!');
      onSuccess();
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-md w-full">
        <div className="border-b border-gray-200 p-6 flex items-center justify-between">
          <h3 className="text-xl font-bold text-gray-900">Edit Team</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
              {error}
            </div>
          )}

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Team Name *
            </label>
            <input
              type="text"
              required
              placeholder="e.g., 14U, 16U"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Description
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              rows="3"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Team Photo
            </label>

            {photoUrl && (
              <div className="mb-3">
                <img
                  src={photoUrl}
                  alt="Team photo"
                  className="w-32 h-32 rounded-lg object-cover"
                />
              </div>
            )}

            <label className="cursor-pointer inline-flex items-center space-x-2 px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition">
              <Upload size={18} />
              <span>{uploading ? 'Uploading...' : 'Upload Photo'}</span>
              <input
                type="file"
                accept="image/*"
                onChange={handlePhotoUpload}
                disabled={uploading}
                className="hidden"
              />
            </label>
          </div>

          <div className="flex space-x-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 border border-gray-300 text-gray-700 py-3 rounded-lg font-medium hover:bg-gray-50 transition"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              className="flex-1 bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition disabled:opacity-50"
            >
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

